fn main() {
    let g = Greeter::new("Hello Rust");
    g.say_hello();
}

Greeting struct {
    Message string
}

func (g *Greeting) Say() {
    fmt.Println(g.Message)
}

func main() {
    g := &Greeting{Message: "Hello Golang"}
    g.Say()
}

