export function formatCodeActionsResult(result: (CodeAction | Command)[] | null): string {
  if (!result || result.length === 0) {
    return 'No code actions available.';
  }

  let output = `Found ${result.length} code action${result.length !== 1 ? 's' : ''}:\n\n`;

  for (let i = 0; i < result.length; i++) {
    const item = result[i];

    if ('title' in item && 'kind' in item) {
      // CodeAction
      output += `${i + 1}. **${item.title}**`;
      if (item.kind) {
        output += ` _(${item.kind})_`;
      }
      output += '\n';
    } else if ('title' in item) {
      // Command
      output += `${i + 1}. **${item.title}**\n`;
    }
  }

  return output;
}

export function formatCompletionResult(result: CompletionList | CompletionItem[] | null): string {
  if (!result) {
    return 'No completions available.';
  }

  const items = Array.isArray(result) ? result : result.items;

  if (items.length === 0) {
    return 'No completions available.';
  }

  let output = `Found ${items.length} completion${items.length !== 1 ? 's' : ''}:\n\n`;

  for (const item of items.slice(0, 50)) {
    output += `- **${item.label}**`;

    if (item.kind !== undefined) {
      const kindName = getCompletionItemKindName(item.kind);
      output += ` _(${kindName})_`;
    }

    if (item.detail) {
      output += `  ${item.detail}`;
    }

    if (item.documentation) {
      const doc = typeof item.documentation === 'string'
        ? item.documentation
        : item.documentation.value;
      const truncated = doc.length > 100 ? doc.substring(0, 100) + '...' : doc;
      output += `\n  ${truncated}`;
    }

    output += '\n';
  }

  if (items.length > 50) {
    output += `\n_...and ${items.length - 50} more_`;
  }

  return output;
}

export function formatDefinitionResult(result: Location | Location[] | LocationLink[] | null): string {
  if (!result) {
    return 'No definition found.';
  }

  const locations = Array.isArray(result) ? result : [result];

  if (locations.length === 0) {
    return 'No definition found.';
  }

  let output = `Found ${locations.length} definition${locations.length !== 1 ? 's' : ''}:\n\n`;

  for (const loc of locations) {
    if ('targetUri' in loc) {
      // LocationLink
      const path = uriToPath(loc.targetUri);
      const range = loc.targetSelectionRange || loc.targetRange;
      output += `- ${path}:${range.start.line + 1}:${range.start.character + 1}\n`;
    } else {
      // Location
      const path = uriToPath(loc.uri);
      output += `- ${path}:${loc.range.start.line + 1}:${loc.range.start.character + 1}\n`;
    }
  }

  return output;
}

function formatDiagnostic(diag: Diagnostic): string {
  let output = `**Line ${diag.range.start.line + 1}:${diag.range.start.character + 1}**: ${diag.message}`;

  if (diag.source) {
    output += ` _(${diag.source})_`;
  }

  if (diag.code) {
    output += ` [${diag.code}]`;
  }

  output += '\n\n';

  return output;
}

export function formatDiagnosticsResult(result: Diagnostic[]): string {
  if (result.length === 0) {
    return 'No diagnostics (errors or warnings) found.';
  }

  const errors = result.filter(d => d.severity === 1);
  const warnings = result.filter(d => d.severity === 2);
  const info = result.filter(d => d.severity === 3);
  const hints = result.filter(d => d.severity === 4);

  let output = `Found ${result.length} diagnostic${result.length !== 1 ? 's' : ''}:\n`;
  output += `- ${errors.length} error${errors.length !== 1 ? 's' : ''}\n`;
  output += `- ${warnings.length} warning${warnings.length !== 1 ? 's' : ''}\n`;
  output += `- ${info.length} info\n`;
  output += `- ${hints.length} hint${hints.length !== 1 ? 's' : ''}\n\n`;

  // Show errors first
  if (errors.length > 0) {
    output += '## Errors\n\n';
    for (const diag of errors) {
      output += formatDiagnostic(diag);
    }
  }

  // Then warnings
  if (warnings.length > 0) {
    output += '## Warnings\n\n';
    for (const diag of warnings) {
      output += formatDiagnostic(diag);
    }
  }

  // Then info/hints if there aren't too many diagnostics
  if (result.length <= 20) {
    if (info.length > 0) {
      output += '## Info\n\n';
      for (const diag of info) {
        output += formatDiagnostic(diag);
      }
    }

    if (hints.length > 0) {
      output += '## Hints\n\n';
      for (const diag of hints) {
        output += formatDiagnostic(diag);
      }
    }
  }

  return output;
}

function formatDocumentSymbol(symbol: DocumentSymbol, indent: number): string {
  const kindName = getSymbolKindName(symbol.kind);
  const line = symbol.range.start.line + 1;
  const indentStr = '  '.repeat(indent);

  let output = `${indentStr}- **${symbol.name}** _(${kindName})_  Line ${line}`;

  if (symbol.detail) {
    output += `  ${symbol.detail}`;
  }

  output += '\n';

  if (symbol.children) {
    for (const child of symbol.children) {
      output += formatDocumentSymbol(child, indent + 1);
    }
  }

  return output;
}

export function formatHoverResult(result: Hover | null): string {
  if (!result || !result.contents) {
    return 'No hover information available.';
  }

  const contents = Array.isArray(result.contents) ? result.contents : [result.contents];

  let output = '';

  for (const content of contents) {
    if (typeof content === 'string') {
      output += content + '\n\n';
    } else if ('language' in content) {
      // MarkedString with language
      output += `\`\`\`${content.language}\n${content.value}\n\`\`\`\n\n`;
    } else if ('value' in content) {
      // MarkupContent
      output += content.value + '\n\n';
    }
  }

  return output.trim();
}

export function formatReferencesResult(result: Location[] | null): string {
  if (!result || result.length === 0) {
    return 'No references found.';
  }

  let output = `Found ${result.length} reference${result.length !== 1 ? 's' : ''}:\n\n`;

  for (const loc of result) {
    const path = uriToPath(loc.uri);
    output += `- ${path}:${loc.range.start.line + 1}:${loc.range.start.character + 1}\n`;
  }

  return output;
}

function formatSymbolInformation(symbol: SymbolInformation): string {
  const kindName = getSymbolKindName(symbol.kind);
  const path = uriToPath(symbol.location.uri);
  const line = symbol.location.range.start.line + 1;

  let output = `- **${symbol.name}** _(${kindName})_`;

  if (symbol.containerName) {
    output += ` in ${symbol.containerName}`;
  }

  output += `  ${path}:${line}\n`;

  return output;
}

export function formatSymbolsResult(result: SymbolInformation[] | DocumentSymbol[] | null): string {
  if (!result || result.length === 0) {
    return 'No symbols found.';
  }

  let output = `Found ${result.length} symbol${result.length !== 1 ? 's' : ''}:\n\n`;

  for (const symbol of result) {
    if ('location' in symbol) {
      // SymbolInformation
      output += formatSymbolInformation(symbol);
    } else {
      // DocumentSymbol
      output += formatDocumentSymbol(symbol, 0);
    }
  }

  return output;
}

export function formatTextEditsResult(result: TextEdit[] | null, filePath: string): string {
  if (!result || result.length === 0) {
    return 'No edits available.';
  }

  let output = `Applied ${result.length} edit${result.length !== 1 ? 's' : ''} to ${filePath}:\n\n`;

  for (const edit of result) {
    const startLine = edit.range.start.line + 1;
    const endLine = edit.range.end.line + 1;

    if (startLine === endLine) {
      output += `- Line ${startLine}: `;
    } else {
      output += `- Lines ${startLine}-${endLine}: `;
    }

    if (edit.newText) {
      const preview = edit.newText.length > 50
        ? edit.newText.substring(0, 50) + '...'
        : edit.newText;
      output += `Replace with: \`${preview}\``;
    } else {
      output += 'Delete';
    }

    output += '\n';
  }

  return output;
}

export function formatWorkspaceEditResult(result: WorkspaceEdit | null): string {
  if (!result) {
    return 'No workspace edit available.';
  }

  let output = 'Workspace edit:\n\n';

  if (result.changes) {
    for (const [uri, edits] of Object.entries(result.changes)) {
      const path = uriToPath(uri);
      output += `### ${path}\n\n`;
      output += `${edits.length} edit${edits.length !== 1 ? 's' : ''}:\n`;

      for (const edit of edits) {
        output += `- Line ${edit.range.start.line + 1}: `;
        if (edit.newText) {
          output += `Replace with: \`${edit.newText}\``;
        } else {
          output += 'Delete';
        }
        output += '\n';
      }

      output += '\n';
    }
  }

  if (result.documentChanges) {
    output += `${result.documentChanges.length} document change${result.documentChanges.length !== 1 ? 's' : ''}\n`;
  }

  return output;
}

export function createServer(workspaceRoot?: string): Server {
  // Create client manager
  const clientManager = new ClientManager(workspaceRoot);

  // Create MCP server
  const server = new Server(
    {
      name: 'mcp-lsp-bridge',
      version: '0.1.0',
    },
    {
      capabilities: {
        tools: {},
      },
    }
  );

  // Register list_tools handler
  server.setRequestHandler(ListToolsRequestSchema, async () => {
    return {
      tools: [
        {
          name: 'lsp_open_document',
          description: 'Open a document in the appropriate LSP server. This must be called before using other LSP tools on a file.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path to open',
              },
            },
            required: ['filePath'],
          },
        },
        {
          name: 'lsp_close_document',
          description: 'Close a document in the LSP server. Call this when done working with a file.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path to close',
              },
            },
            required: ['filePath'],
          },
        },
        {
          name: 'lsp_completion',
          description: 'Get code completions at a specific position in a document. Returns suggestions for code that can be inserted at the position.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              line: {
                type: 'number',
                description: 'Line number (zero-based)',
              },
              character: {
                type: 'number',
                description: 'Character offset on the line (zero-based)',
              },
            },
            required: ['filePath', 'line', 'character'],
          },
        },
        {
          name: 'lsp_hover',
          description: 'Get hover information (types, documentation) at a specific position in a document.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              line: {
                type: 'number',
                description: 'Line number (zero-based)',
              },
              character: {
                type: 'number',
                description: 'Character offset on the line (zero-based)',
              },
            },
            required: ['filePath', 'line', 'character'],
          },
        },
        {
          name: 'lsp_definition',
          description: 'Go to the definition of a symbol at a specific position. Returns the location(s) where the symbol is defined.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              line: {
                type: 'number',
                description: 'Line number (zero-based)',
              },
              character: {
                type: 'number',
                description: 'Character offset on the line (zero-based)',
              },
            },
            required: ['filePath', 'line', 'character'],
          },
        },
        {
          name: 'lsp_references',
          description: 'Find all references to a symbol at a specific position. Returns all locations where the symbol is used.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              line: {
                type: 'number',
                description: 'Line number (zero-based)',
              },
              character: {
                type: 'number',
                description: 'Character offset on the line (zero-based)',
              },
              includeDeclaration: {
                type: 'boolean',
                description: 'Include the declaration in the results (default: true)',
              },
            },
            required: ['filePath', 'line', 'character'],
          },
        },
        {
          name: 'lsp_diagnostics',
          description: 'Get diagnostics (errors, warnings, info, hints) for a document.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
            },
            required: ['filePath'],
          },
        },
        {
          name: 'lsp_rename',
          description: 'Rename a symbol across the entire workspace. Returns a workspace edit with all the changes.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              line: {
                type: 'number',
                description: 'Line number (zero-based)',
              },
              character: {
                type: 'number',
                description: 'Character offset on the line (zero-based)',
              },
              newName: {
                type: 'string',
                description: 'New name for the symbol',
              },
            },
            required: ['filePath', 'line', 'character', 'newName'],
          },
        },
        {
          name: 'lsp_format_document',
          description: 'Format an entire document according to the language server\'s formatting rules.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              tabSize: {
                type: 'number',
                description: 'Number of spaces per tab (default: 2)',
              },
              insertSpaces: {
                type: 'boolean',
                description: 'Use spaces instead of tabs (default: true)',
              },
            },
            required: ['filePath'],
          },
        },
        {
          name: 'lsp_code_action',
          description: 'Get available code actions (quick fixes, refactorings) for a range in a document.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
              startLine: {
                type: 'number',
                description: 'Start line number (zero-based)',
              },
              startCharacter: {
                type: 'number',
                description: 'Start character offset (zero-based)',
              },
              endLine: {
                type: 'number',
                description: 'End line number (zero-based)',
              },
              endCharacter: {
                type: 'number',
                description: 'End character offset (zero-based)',
              },
            },
            required: ['filePath', 'startLine', 'startCharacter', 'endLine', 'endCharacter'],
          },
        },
        {
          name: 'lsp_workspace_symbols',
          description: 'Search for symbols across the entire workspace. Useful for finding classes, functions, variables, etc.',
          inputSchema: {
            type: 'object',
            properties: {
              query: {
                type: 'string',
                description: 'Search query for symbols (empty string to get all symbols)',
              },
            },
            required: ['query'],
          },
        },
        {
          name: 'lsp_document_symbols',
          description: 'Get the symbol outline/structure of a document. Returns a hierarchical list of symbols.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path',
              },
            },
            required: ['filePath'],
          },
        },
        {
          name: 'project_index',
          description: 'Recursively index the project workspace to discover files and directories.',
          inputSchema: {
            type: 'object',
            properties: {
              rootPath: {
                type: 'string',
                description: 'Root directory to start indexing from (default: workspace root)',
              },
              maxDepth: {
                type: 'number',
                description: 'Maximum recursion depth (default: 10)',
              },
              exclude: {
                type: 'array',
                items: { type: 'string' },
                description: 'Directory names to exclude (node_modules, .git, etc.)',
              },
            },
          },
        },
        {
          name: 'read_and_chunk',
          description: 'Read a file and split it into text chunks for processing.',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path to read',
              },
              chunkSize: {
                type: 'number',
                description: 'Character count per chunk (default: 2000)',
              },
              overlap: {
                type: 'number',
                description: 'Character overlap between chunks (default: 200)',
              },
            },
            required: ['filePath'],
          },
        },
        {
          name: 'semantic_chunk',
          description: 'Parse a document into semantic chunks based on the code structure (classes, functions, etc.).',
          inputSchema: {
            type: 'object',
            properties: {
              filePath: {
                type: 'string',
                description: 'Absolute file path to chunk',
              },
            },
            required: ['filePath'],
          },
        },
      ],
    };
  });

  // Register call_tool handler
  server.setRequestHandler(CallToolRequestSchema, async (request) => {
    const { name, arguments: args } = request.params;

    try {
      let result: string;

      switch (name) {
        case 'lsp_open_document': {
          const parsed = OpenDocumentArgsSchema.parse(args);
          result = await handleOpenDocument(parsed, clientManager);
          break;
        }

        case 'lsp_close_document': {
          const parsed = CloseDocumentArgsSchema.parse(args);
          result = await handleCloseDocument(parsed, clientManager);
          break;
        }

        case 'lsp_completion': {
          const parsed = CompletionArgsSchema.parse(args);
          result = await handleCompletion(parsed, clientManager);
          break;
        }

        case 'lsp_hover': {
          const parsed = HoverArgsSchema.parse(args);
          result = await handleHover(parsed, clientManager);
          break;
        }

        case 'lsp_definition': {
          const parsed = DefinitionArgsSchema.parse(args);
          result = await handleDefinition(parsed, clientManager);
          break;
        }

        case 'lsp_references': {
          const parsed = ReferencesArgsSchema.parse(args);
          result = await handleReferences(parsed, clientManager);
          break;
        }

        case 'lsp_diagnostics': {
          const parsed = DiagnosticsArgsSchema.parse(args);
          result = await handleDiagnostics(parsed, clientManager);
          break;
        }

        case 'lsp_rename': {
          const parsed = RenameArgsSchema.parse(args);
          result = await handleRename(parsed, clientManager);
          break;
        }

        case 'lsp_format_document': {
          const parsed = FormatDocumentArgsSchema.parse(args);
          result = await handleFormatDocument(parsed, clientManager);
          break;
        }

        case 'lsp_code_action': {
          const parsed = CodeActionArgsSchema.parse(args);
          result = await handleCodeAction(parsed, clientManager);
          break;
        }

        case 'lsp_workspace_symbols': {
          const parsed = WorkspaceSymbolsArgsSchema.parse(args);
          result = await handleWorkspaceSymbols(parsed, clientManager);
          break;
        }

        case 'lsp_document_symbols': {
          const parsed = DocumentSymbolsArgsSchema.parse(args);
          result = await handleDocumentSymbols(parsed, clientManager);
          break;
        }

        case 'project_index': {
          const parsed = ProjectIndexArgsSchema.parse(args);
          result = await handleProjectIndex(parsed, clientManager.workspaceRootPath);
          break;
        }

        case 'read_and_chunk': {
          const parsed = ReadAndChunkArgsSchema.parse(args);
          result = await handleReadAndChunk(parsed);
          break;
        }

        case 'semantic_chunk': {
          const parsed = SemanticChunkArgsSchema.parse(args);
          result = await handleSemanticChunk(parsed, clientManager);
          break;
        }

        default:
          throw new Error(`Unknown tool: ${name}`);
      }

      return {
        content: [
          {
            type: 'text',
            text: result,
          },
        ],
      };
    } catch (error) {
      const message = error instanceof Error ? error.message : String(error);
      logger.error(`Tool ${name} error:`, error);

      return {
        content: [
          {
            type: 'text',
            text: `Error: ${message}`,
          },
        ],
        isError: true,
      };
    }
  });

  // Handle server shutdown
  process.on('SIGINT', async () => {
    logger.info('Shutting down MCP server...');
    await clientManager.shutdown();
    process.exit(0);
  });

  process.on('SIGTERM', async () => {
    logger.info('Shutting down MCP server...');
    await clientManager.shutdown();
    process.exit(0);
  });

  return server;
}

